<template>
  <div>
    <div class="carousel-wrap">
      <transition-group
        tag="ul"
        class="slide-ul"
        name="slide"
      >
        <li
          v-for="(item, index) in sliders"
          :key="index"
          v-show="index === currentIndex"
          @mouseenter="stop"
          @mouseleave="go"
        >
          <a :href="item.url"><img
              :src="item.image"
              alt="item.description"
            /></a>
        </li>
      </transition-group>
      <div class="carousel-items"><span
          v-for="(item, index) in sliders"
          :key="index"
          :class="{ active: index === currentIndex }"
          @mouseover="change(index)"
        ></span></div>
    </div>
  </div>
</template>
<script>
export default {
  data() {
    return {
      logisActive: true,
      albumisActive: false,
      logistrue: true,
      albumistrue: false,
      /*轮播的效果*/
      sliders: [
        {
          url: "#",
          description: "one",
          image:
            "data:image/png;base64,"
        },
        {
          url: "#",
          description: "two",
          image:
            "data:image/jpeg;base64,"
        },
        {
          url: "#",
          description: "three",
          image:
            "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQoXd_zkyQNqTjEPwUeu0NFb_rTvqO1ewsUCOB77YFZ1-u-3AOb"
        }
      ],
      currentIndex: 0,
      timer: null
    };
  },
  methods: {
    go() {
      this.timer = setInterval(() => {
        this.autoPlay();
      }, 3000);
    },
    stop() {
      clearInterval(this.timer);
      this.timer = null;
    },
    change(index) {
      this.currentIndex = index;
    },
    autoPlay() {
      this.currentIndex++;
      if (this.currentIndex > this.sliders.length - 1) {
        this.currentIndex = 0;
      }
    },
    logclick() {
      this.logistrue = true;
      this.albumistrue = false;
      this.logisActive = true;
      this.albumisActive = false;
    },
    albumclick() {
      this.logistrue = false;
      this.albumistrue = true;
      this.logisActive = false;
      this.albumisActive = true;
    }
  }
};
</script>
<style>
/*轮播的效果*/
.carousel-wrap {
  width: 100%;
  height: 720px;
  position: absolute;
  top: 450px;
  margin-left: 110px;
}

.slide-ul {
  width: 100%;
  height: 100%;
}

.slide-ul li {
  position: absolute;
  top: 0;
  width: 100%;
  height: 100%;
}

.slide-ul li img {
  width: 50%;
  height: 50%;
}

.carousel-items {
  z-index: 100;
  margin-left: 300px;
  position: relative;
  font-size: 0;
  margin-top: -200px;
}

.carousel-items span {
  display: inline-block;
  width: 50px;
  height: 6px;
  margin: 0 5px;
  background-color: #eee;
  cursor: pointer;
}

.carousel-items .active {
  background-color: #ffa500;
}

/* 动画 */
.slide-enter-to {
  transition: all 1s ease;
  transform: translateX(0);
}

.slide-leave-active {
  transition: all 1s ease;
  transform: translateX(-100%);
}

.slide-enter {
  transform: translateX(100%);
}

.slide-leave {
  transform: translateX(0);
}
</style>
